window.addEventListener("load",e=>{window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches&&null===document.documentElement.getAttribute("data-theme")&&document.documentElement.setAttribute("data-theme","dark"),document.querySelectorAll(".navbar-collapse").forEach(e=>{e.addEventListener("click",e=>{let t=e.target||e.srcElement;t=t.parentElement.querySelector(".navbar-items"),t.classList.toggle("collapsed")})}),document.querySelectorAll(".darkmode-toggler").forEach(e=>{e.onchange=()=>{e.checked?document.documentElement.setAttribute("data-theme","dark"):document.documentElement.setAttribute("data-theme","light")}})});